import React, { useState } from "react";
import { connect } from "react-redux";
import { useForm } from "react-hook-form";
import { registerNewUserAction } from "../../Redux/actions/Actions";
import "./SignUp.css";
const SignUp = props => {
  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmit = data => {
    console.log(data);
  };

  return (
    <div className="register-form">
      <div className="left-register">
        <div className="register-left">
          <h1>Good to see you!</h1>
          <p>
            By creating an account on KingFut, you agree to our Terms of use and
            Privacy Policy.
          </p>
        </div>
      </div>
      <div className="right-register">
        <div className="register-right">
          <h3>Register</h3>
          <form onSubmit={handleSubmit(onSubmit)}>
            <label for="email"> Email or User Name </label>
            <input
              id="email"
              placeholder="Type your username"
              ref={register}
            ></input>
            <label for="password">Password </label>
            <input
              id="password"
              placeholder="Type your password"
              type="password"
              ref={register}
            ></input>
            <label for="gender">Gender</label>
            <input name="gender" ref={register} list="list"></input>
            <datalist id="list">
              <option value="Male" />
              <option value="Female" />
            </datalist>
            <button onClick={() => submitUser()}> Register </button>
            <a> Already have an account </a>
          </form>
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = state => ({});
const mapDispatchToProps = dispatch => ({});
export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
