import React, { useState } from "react";
import { connect } from "react-redux";
import { registerNewUserAction } from "../../Redux/actions/Actions";
import "./SignUp.css";
const SignUp = props => {
  const [userName, getUserName] = useState("");
  const [password, getPassword] = useState("");
  const [gender, getGender] = useState("");

  const User = {
    newUserName: userName,
    newUserPassword: password,
    newUserGender: gender
  };
  const submitUser = () => {
    props.register(userName, password, gender);
  };
  return (
    <div className="register-form">
      <div className="left-register">
        <div className="register-left">
          <h1>Good to see you!</h1>
          <p>
            By creating an account on KingFut, you agree to our Terms of use and
            Privacy Policy.
          </p>
        </div>
      </div>
      <div className="right-register">
        <div className="register-right">
          <h3>Register</h3>

          <label for="email"> Email or User Name </label>
          <input
            id="email"
            placeholder="Type your username"
            onChange={e => getUserName(e.target.value)}
          ></input>
          <label for="password">Password </label>
          <input
            id="password"
            placeholder="Type your password"
            type="password"
            onChange={e => getPassword(e.target.value)}
          ></input>
          <label for="gender">Gender</label>
          <input onChange={e => getGender(e.target.value)} list="list"></input>
          <datalist id="list">
            <option value="Male" />
            <option value="Female" />
          </datalist>
          <button onClick={() => submitUser()}> Register </button>
          <a> Already have an account </a>
        </div>
      </div>
    </div>
  );
};
const mapStateToProps = state => ({});
const mapDispatchToProps = dispatch => ({
  register: (userName, password, gender) =>
    dispatch(registerNewUserAction(userName, password, gender))
});
export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
