import React, { useEffect } from "react";
import { fetchLeagueTableAction } from "../../Redux/actions/Actions";
import { useLocation } from "react-router-dom";
import { connect } from "react-redux";
const Table = props => {
  useEffect(() => {
    props.fetchTable(leagueToRender);
  }, []);
  const leagueToRender = useLocation().pathname.slice(8);
  console.log(props.table);
  return (
    <div className="table">
      <div className="header">
        <p>Season</p>
        <p className="season"></p>
      </div>
      <div className="columns">
        <p className="club-column">Club</p>
        <p className="game-played">MP</p>
        <p className="win">W</p>
        <p className="draw">D</p>
        <p className="lost">L</p>
        <p className="goals-for">GF</p>
        <p className="goals-against">GA</p>
        <p className="goals-difference">GD</p>
        <p className="points">Pts</p>
      </div>
    </div>
  );
};
const mapStateToProps = state => ({
  table: state.Table
});
const mapDispatchToProps = dispatch => ({
  fetchTable: leagueToRender => dispatch(fetchLeagueTableAction(leagueToRender))
});
export default connect(mapStateToProps, mapDispatchToProps)(Table);
