import Axios from "axios";
import { fetchEnglishLeagueTable } from "./ActionTypes";

export function fetchEnglishMatches() {
  var date = new Date();
  var day = ("0" + date.getDate()).slice(-2);
  var month = ("0" + date.getMonth()).slice(-2);
  var url = `https://api.football-data.org/v2/competitions/PL/matches?dateFrom=2020-${month}-${day}&dateTo=2020-03-07`;
  var token = "52c8d88969d84ac9b17edb956eea33af";
  var obj = {
    headers: { "X-Auth-Token": token }
  };
  return dispatch => {
    return Axios.get(url, obj)
      .then(res => {
        dispatch({
          type: fetchEnglishLeagueTable,
          payload: res.data.matches
        });
      })
      .catch(e => {
        console.log("Cant fetch ", e);
      });
  };
}
