import React from "react";
import "./HomePage.css";
const HomePage = () => {
  var slideIndex = 0;
  showSlides();

  function showSlides() {
    var i;
    var slides = document.getElementsByClassName("mySlides");
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    slideIndex++;
    if (slideIndex > slides.length) {
      slideIndex = 1;
    }
    slides[slideIndex - 1].style.display = "block";
    setTimeout(showSlides, 2000); // Change image every 2 seconds
  }
  return (
    <div>
      <div className="slideshow-container">
        <div className="mySlides fade">
          <img src="https://i.insider.com/5e1452d6855cc227cd527362?width=1100&format=jpeg&auto=webp"></img>
          <div className="text">Caption Text</div>
        </div>
        <div className="mySlides fade">
          <img src="https://www.fcbarcelonanoticias.com/uploads/s1/11/83/40/0/messi-balon-oro-merece.jpeg"></img>
          <div className="text">Caption Text</div>
        </div>
        <div className="mySlides fade">
          <img src="https://s3.tvp.pl/images2/c/b/a/uid_cba846a61faa59f7a44b42a59cce22a41555576941412_width_1137_play_0_pos_0_gs_0_height_640.jpg"></img>
          <div className="text">Caption Text</div>
        </div>
        <div className="mySlides fade">
          <img src="https://pbs.twimg.com/media/EF0wMKPWoAE2XP1.jpg"></img>
          <div className="text">Caption Text</div>
        </div>
        <div>
          <span className="dot" onclick="currentSlide(1)"></span>
          <span className="dot" onclick="currentSlide(2)"></span>
          <span className="dot" onclick="currentSlide(3)"></span>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
