import {
  fetchMatches,
  fetchLeagueTable,
  fetchTopScorers
} from "../actions/ActionTypes";
const initialState = {
  Matches: {},
  Table: {},
  Scorers: {}
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case fetchMatches:
      return {
        ...state,
        Matches: action.payload
      };
    case fetchLeagueTable:
      return {
        ...state,
        Table: action.payload
      };
    case fetchTopScorers:
      return {
        ...state,
        Scorers: action.payload
      };
    default:
      return state;
  }
};

export default rootReducer;
