import React, { useEffect } from "react";
import { connect } from "react-redux";
import { fetchMatchesAction } from "../../Redux/actions/Actions";
import Matches from "../matches/Matches";
import Table from "../Table/Table";
import TopScorer from "../TopScorer/TopScorer";
import { useLocation } from "react-router-dom";

const League = props => {
  useEffect(() => {
    props.getLeagueTable(leagueToRender);
  }, []);
  const leagueToRender = useLocation().pathname.slice(8);
  console.log(props.leagueTable);
  const leaguesNames = {
    PL: "English Premier League",
    PD: "Spanish LaLiga",
    BL1: "German Bundesliga",
    SA: "Italian Serie A"
  };
  const leaguesInformation = {
    PL: "",
    PD: "",
    BL1: "",
    SA: ""
  };
  const leaguesCountriesImages = {
    PL: "",
    PD: "https://upload.wikimedia.org/wikipedia/en/9/9a/Flag_of_Spain.svg",
    BL1: "",
    SA: "https://upload.wikimedia.org/wikipedia/en/0/03/Flag_of_Italy.svg"
  };
  return (
    <div className="league-header">
      <h2>{leaguesNames.PL}</h2>
      <h4>aidioad</h4>
      <h5>Check Matches Dates and Results</h5>
      <Matches />
      <Table />
      <TopScorer />
    </div>
  );
};
const mapStateToProps = state => ({
  leagueTable: state.EnglishTable
});
const mapDispatchToProps = dispatch => {
  return {
    getLeagueTable: leagueToRender =>
      dispatch(fetchMatchesAction(leagueToRender))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(League);
