import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";
import { addCommentAction } from "../../Redux/actions/Actions";
import "./Predict.css";
const Predict = (props) => {
  const [commentMessage, setCommentMessage] = useState("");
  const session = sessionStorage.getItem("storedSession");
  const history = useHistory();
  useEffect(() => {
    if (!session) {
      history.push("/login");
    }
  }, []);

  const submitComment = () => {
    let match = props.location.pathname.slice(10);
    let username = session;
    let commentData = {
      match: match,
      username: username,
      comment: props.commentMessage,
    };
  };

  return (
    <div className="comments-section">
      <div className="add-comment">
        <input></input>
        <button onClick={(e) => submitComment()}>comment</button>
      </div>
      <div className="prev-comments">
        <div className="comment">
          <p>asdasd</p>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({});
const mapDispatchToProps = (dispatch) => ({
  comment: (commentData) => dispatch(addCommentAction(commentData)),
});
export default connect(mapStateToProps, mapDispatchToProps)(Predict);
