import { fetchMatches, fetchLeagueTable } from "../actions/ActionTypes";
const initialState = {
  Matches: {},
  Table: {}
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case fetchMatches:
      return {
        ...state,
        Matches: action.payload
      };
    case fetchLeagueTable:
      return {
        ...state,
        Table: action.payload
      };
    default:
      return state;
  }
};

export default rootReducer;
