import React from "react";
import { connect } from "react-redux";
import { useForm } from "react-hook-form";
import { loginUserAction } from "../../Redux/actions/Actions";
import "./SignIn.css";
const SignIn = props => {
  const { login, handleSubmit, watch, errors } = useForm();

  const onSubmit = data => {
    const userData = {
      email: data.email,
      password: data.password
    };

    props.login(userData);
  };
  console.log(props.login);

  return (
    <div className="signin-form">
      <div className="left-signin">
        <div className="left-login">
          <h1>Good to see you again!</h1>
          <p>
            By logging into KingFut, you agree to our Terms of use and Privacy
            Policy.
          </p>
        </div>
      </div>
      <div className="right-signin">
        <div className="right-info">
          <h3>Sign In</h3>
          <form onSubmit={handleSubmit(onSubmit)}>
            <label for="email"> Email </label>
            <input
              type="text"
              placeholder="Enter your email"
              name="email"
              id="email"
            />
            <label for="password" required>
              Password
            </label>
            <input
              id="password"
              placeholder="Enter your password"
              name="password"
              type="password"
              ref={login({ required: true })}
            />
            <input type="submit" ref={register({ required: true })} />
          </form>
          <a> Create an account </a>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = state => ({
  user: state.Session
});
const mapDispatchToProps = dispatch => ({
  login: userData => dispatch(loginUserAction(userData))
});
export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
